import './App.css';
import { React, Component } from "react"
import  Search from "./Search/Search";
import  SearchResult from "./SearchResult/SearchResult";
import  NoResult from "./SearchResult/NoResult";
import axios from 'axios';

const API_KEY = process.env.REACT_APP_OMDB_API_KEY;


class App extends Component  {
state = {
  userInput: "", 
  result: [{Title: null,}]
}

inputHandler = (event) => {
  let moviesResult = []

  axios.get(`https://www.omdbapi.com/?s=${event.target.value}&apikey=${API_KEY}`) 
  .then(res => {
    this.setState({userInput: `Search result for "${event.target.value}":`})
    if(res.data.Search !== undefined){
      for(let movies of res.data.Search.slice(0,6)) {
        moviesResult.push(movies)
      }
      this.setState( 
        {result: moviesResult} ) 
    }  

    console.log(this.state.result)
  })
}


render (){

  let persons = null; 

    if (this.state.showPersons) {

      let personState = (person, index) => {
        return <SearchResult
          click = {() => this.deletePersonHandler(index)}
          name = {person.name}
          age = {person.age}
          key = {person.id}
          changed = {(event) =>this.nameChangedHandler(event, person.id)}
        />
      }

      persons = (
          <div >
            {this.state.persons.map(personState)}
          </div> 
      )
    } 


  return (
  <div className="App">
    <Search 
    input = {this.inputHandler} 
    ></Search>
  { this.state.userInput.length >= 22 ?
   <SearchResult 

   title = {this.state.result}>
   </SearchResult> : <NoResult></NoResult>
  }
  </div>
  );
  } 
}

export default App;
